import React from "react";
import { useEffect } from "react";
import { useReducer } from "react";
import { createContext } from "react";

export const gameContext = createContext();

export function GameProvider(props){

    const ACTION = {
        LEVEL: 'level',
        APPEND: 'append',
        DEL: 'delete',
        ADD: 'add',
    }

    const WORD_LIST = ["abcdef",'abroad','answer','Choose','Attend','Chance','August',
    'Advice','Almost','Answer','Behind','Desert','Appeal'];
    let answer;

    // useEffect(()=>{
    //     let answer = WORD_LIST[Math.floor(Math.random()* WORD_LIST.length)];
    //     console.log(answer);
    // }, [answer]);


    function game_state_reducer(state, action){
        switch(action.type){
            case 'level':
                return {...state,
                    isHard: !state.isHard,
                    chance: state.isHard ? 6 : 5, 
                    letter_guess: state.isHard ? 6 : 7};
            case 'append':
                console.log(state.temp_guess);
                return{...state,
                temp_guess: (state.curr_index === state.letter_guess)? state.temp_guess : state.temp_guess.concat(action.payload),
                curr_index: (state.curr_index === state.letter_guess)? state.curr_index: state.curr_index + 1};
            case 'delete':
                return {...state,
                    temp_guess: state.temp_guess.slice(0,-1),
                    curr_index: (state.curr_index === 0) ? 0: state.curr_index -1
                };
            case "add":
                return{...state,
                    guessed_list: state.guessed_list.add(action.payload),
                    curr_index: 0,
                    temp_guess: ''
                }
            case "start":
                return{...state,
                word: answer}
            default:
                return state;
        }
    }
    const[game_state, dispatch] = useReducer(game_state_reducer, 
        {isHard: false, chance: 6, letter_guess: 6, temp_guess: "", guessed_list:[], word: answer,
        curr_index: 0});

    console.log(answer)
    return(
        <gameContext.Provider value={{game_state, dispatch, ACTION}}>
            {props.children}
        </gameContext.Provider>
    )

}
