import React from "react";
import { useReducer } from "react";
import { useState } from "react";
import { createContext } from "react";

export const gameContext = createContext();

export function GameProvider(props){

    const[isHard, setLevel] = useState(false);

    const ACTION = {
        LEVEL: 'level',

    }

    function game_state_reducer(state, action){
        switch(action.type){
            case 'level':
                return {...state, isHard: !state.isHard}
        }
    }
    const[game_state, dispatch] = useReducer(game_state_reducer, {isHard: false, });

    return(
        <gameContext.Provider value={{isHard, setLevel}}>
            {props.children}
        </gameContext.Provider>
    )

}
