import React from "react";
import wordle from "./wordle.png";
import './Welcome.css';
import { useNavigate } from "react-router-dom";
import { useContext } from "react";
import {gameContext} from "./GameProvider"

export default function Welcome(props){
    const navigate = useNavigate();
    const context = useContext(gameContext);

    let setLevel = context.setLevel;
    let isHard = context.isHard;

    function onClick_rule(){
        navigate('/rules');
    }

    let game_path;

    if(isHard === false){
        game_path = '/game/normal';

    }else{
        game_path = ''
    }

    function onClick_game(){
        if(isHard === false){
            navigate('/game/normal');
        }else{
            navigate('/game/hard');
        }

    }

    function onChange_level(){
        setLevel(current => !current);
    }


    console.log(isHard)
    return(
        <div className="layout">
            <div className="top_bar">
                <div className="mode_title">Hard mode:</div>
                <label className="switch">
                    <input type="checkbox" onChange={onChange_level} checked={isHard}></input>
                    <span className="slider round"></span>
                </label>
            </div>
            <div className="title">Wordle Game </div>
            <img src={wordle} alt="Logo" className="logo" />
            <div className="subtitle">Get 6 chances to guess a 6-letter word.</div>
            <button className="button_welcome" onClick={onClick_game}>
                Play
            </button>
            <button className="button_welcome" onClick={onClick_rule}>
                Rule
            </button>
        </div>
    )
}
