import { React, useContext, useState, useEffect, useRef } from 'react';
import './game.css';
import { useNavigate } from 'react-router-dom';
import { gameContext } from './GameProvider';
import './modal.css';

export default function Normal(props) {
  const navigate = useNavigate();
  const context = useContext(gameContext);

  let dispatch = context.dispatch;
  let game_state = context.game_state;
  let ACTION = context.ACTION;
  let guess = game_state.temp_guess;
  let guessed_list = game_state.guessed_list;
  let length = game_state.letter_guess;
  let answer = game_state.word;
  let index = game_state.curr_index;

//   const ref_index = useRef(index);

  function goback() {
    navigate(-1);
  }

  function onClick_rule() {
    navigate('/rules');
  }

  useEffect(() => {
    const keys = document.querySelectorAll('.key:not(.delete,.enter)');
    keys.forEach((key) => {
      key.addEventListener('click', onClick_key);
    });
    const enter = document.querySelector('.enter');
    enter.addEventListener('click', onClick_enter);
    const del = document.querySelector('.delete');
    del.addEventListener('click', onClick_delete);

    console.log('key are attached!');
    return () => {
      keys.forEach((key) => {
        key.removeEventListener('click', onClick_key);
      });
      enter.removeEventListener('click', onClick_enter);
      del.removeEventListener('click', onClick_delete);
      console.log('unmounts!!');
    };
  }, [guess]); 

//   useEffect(() => {
//     console.log("guess changed!")
//   },[guess]);

  useEffect(() => {
    let space = document.querySelector('.guess_row_normal:not(.used)');
    space.classList.add('using');
    console.log('next row is fetched');
  }, [guessed_list]);


  const [modal, setModal] = useState(false);
  const [enter_modal, setModal_enter] = useState(false);

  function onClick_unable_change() {
    setModal(!modal);
  }

  function onClick_unable_enter() {
    setModal_enter(!enter_modal);
  }

  function onClick_key(e) {
    let value = e.target.innerText;
    let curr_row = document.querySelector('.using');
    // console.log("before dispatch: " + guess);
    dispatch({ type: ACTION.APPEND, payload: value });
    let curr = curr_row.querySelectorAll('.guess')[index];
    // let x = {...guess};
    // console.log(x);
    curr.innerText = value;
  }

  function onClick_enter() {
    if (guess.length < length) {
      setModal_enter(!enter_modal);
    } else if (guess.length === length) {
      let tmp = {...guess};  // how to pass in value
      check_show_word(tmp);
      // show position correctness
      if (answer.toLowerCase() === guess.toLowerCase()) {
        console.log('You Win!');
        // should show modal box and could exit or restart
      } else {
      }
      dispatch({type: ACTION.ADD, payload: tmp})
    }
  }

  function onClick_delete() {
    let curr_row = document.querySelector('.using');
    console.log(index);
    let curr = curr_row.querySelectorAll('.guess')[index - 1];
    curr.innerText = "";
    dispatch({ type: ACTION.DEL });
  }

  function check_show_word(str) {
    let curr_box = document.querySelector('.using');
    for (let i = 0; i < { length }; i++) {
        console.log(i);
    }
  }

  const first_row = ["q", 'w'];



  return (
    <>
      <header className="navbar">
        <button className="button" onClick={goback}>
          {' '}
          Back{' '}
        </button>
        <div className="longer">
          <button className="button" onClick={onClick_rule}>
            {' '}
            Rule{' '}
          </button>
        </div>
        <div className="mode_title">Hard mode:</div>
        <label className="switch">
          <input
            type="checkbox"
            onClick={onClick_unable_change}
            checked={game_state.isHard}
          ></input>
          <span className="slider round"></span>
        </label>
      </header>

      {enter_modal && (
        <div className="modal">
          <div onClick={onClick_unable_enter} className="overlay"></div>
          <div className="modal-content">
            <h2>Not Enough Letters!</h2>
            <button className="close-modal" onClick={onClick_unable_enter}>
              CLOSE
            </button>
          </div>
        </div>
      )}

      {modal && (
        <div className="modal">
          <div onClick={onClick_unable_change} className="overlay"></div>
          <div className="modal-content">
            <h2>Hello Wordler!</h2>
            <p>You can only change the level before game start!</p>
            <button className="close-modal" onClick={onClick_unable_change}>
              CLOSE
            </button>
          </div>
        </div>
      )}

      <div className="game_layout">
        <div className="guess_board_container">
          <div className="guess_board_normal">
            <div className="guess_row_normal">
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
            </div>

            <div className="guess_row_normal">
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
            </div>

            <div className="guess_row_normal">
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
            </div>
            <div className="guess_row_normal">
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
            </div>
            <div className="guess_row_normal">
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
            </div>
            <div className="guess_row_normal">
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
              <div className="guess"></div>
            </div>
          </div>
        </div>
        <div className="type_board">
          <div className="key_board">
            <button type="button" data-key={'q'} className="key">
              q
            </button>
            <button className="key">w</button>
            <button className="key">e</button>
            <button className="key">r</button>
            <button className="key">t</button>
            <button className="key">y</button>
            <button className="key">u</button>
            <button className="key">i</button>
            <button className="key">o</button>
            <button className="key">p</button>
          </div>
          <div className="key_board">
            <div className="key_space"></div>
            <button className="key">a</button>
            <button className="key">s</button>
            <button className="key">d</button>
            <button className="key">f</button>
            <button className="key">g</button>
            <button className="key">h</button>
            <button className="key">j</button>
            <button className="key">k</button>
            <button className="key">l</button>
            <div className="key_space"></div>
          </div>
          <div className="key_board">
            <button className="key enter">enter</button>
            <button className="key">z</button>
            <button className="key">x</button>
            <button className="key">c</button>
            <button className="key">v</button>
            <button className="key">b</button>
            <button className="key">n</button>
            <button className="key">m</button>
            <button className="key delete">‚Üê</button>
          </div>
        </div>
      </div>
    </>
  );
}
