import React from "react";
import { useEffect } from "react";
import { useReducer } from "react";
import { createContext } from "react";

export const gameContext = createContext();

export function GameProvider(props){

    const ACTION = {
        LEVEL: 'level',

    }

    const WORD_LIST = ["abcdef",'abroad','answer','Choose','Attend','Chance','August',
    'Advice','Almost','Answer','Behind','Desert','Appeal'];


    useEffect(()=>{
        let answer = WORD_LIST[Math.floor(Math.random()* WORD_LIST.length)];
        console.log(answer);
    }, []);


    function game_state_reducer(state, action){
        switch(action.type){
            case 'level':
                return {...state,
                    isHard: !state.isHard,
                    chance: state.isHard ? 6 : 5, 
                    letter_guess: state.isHard ? 6 : 7};
            default:
                return state;
        }
    }
    const[game_state, dispatch] = useReducer(game_state_reducer, 
        {isHard: false, chance: 6, letter_guess: 6, curr_row: 1, temp_guess: "" });

    return(
        <gameContext.Provider value={{game_state, dispatch, ACTION}}>
            {props.children}
        </gameContext.Provider>
    )

}
