  for(let i = 0 ; i < 2; i++){
    const temp = first_row[i];
    const button_com = (<button className='key' onClick={()=>{
        onClick_key(temp);
    }}>{temp}</button>);
    first_row_com.push(button_com);
  }

  const row_wrap = (<div className='key_board'>{first_row_com}</div>);

  first_row_com.push(<button className='key' onClick={()=>{
    onClick_key("enter");
    }}>enter</button>)

  const guess_row_com = [];
  for(let i = 0; i < guess.length; i++){
    const guess_com = (<div className='guess'>{guess[i]}</div>)
    guess_row_com.push(guess_com);
  }

