import React from "react";
import { useReducer } from "react";
import { useState } from "react";
import { createContext } from "react";

export const gameContext = createContext();

export function GameProvider(props){

    const ACTION = {
        LEVEL: 'level',

    }

    function game_state_reducer(state, action){
        switch(action.type){
            case 'level':
                return {...state,
                    isHard: !state.isHard,
                    chance: state.isHard ? 6 : 5, 
                    letter_guess: state.isHard ? 6 : 7};
            default:
                return state;
        }
    }
    const[game_state, dispatch] = useReducer(game_state_reducer, {isHard: false, chance: 6, letter_guess: 6});

    return(
        <gameContext.Provider value={{game_state, dispatch}}>
            {props.children}
        </gameContext.Provider>
    )

}
